const n=(e,t="")=>{const r=new Date(e);switch(t){case"dd/mm/yyyy":return r.toLocaleDateString("pt-BR");case"hh:mm":return r.toLocaleTimeString("pt-BR");case"dd/mm/yyyy hh:mm":return`${r.toLocaleDateString("pt-BR")} ${r.toLocaleTimeString("pt-BR",{hour:"2-digit",minute:"2-digit"})}`;default:return r.toLocaleDateString("pt-BR")}},o=e=>{if(!e)return null;const t=e.split("/");return t.length!==3?e:`${t[2]}-${t[1]}-${t[0]}`};export{n as _,o as f};
